import 'package:flutter/material.dart';

class Movie {
  final String title;
  final String releaseYear;
  final String posterUrl;
  final double rating;

  Movie({required this.title, required this.releaseYear, required this.posterUrl, required this.rating});
}

class MyAppHome extends StatelessWidget {
  final List<Movie> movies = [
    Movie(title: "A man called Otto", releaseYear: "2023", posterUrl: "https://m.media-amazon.com/images/M/MV5BNzg3OTEzMTgtYWU0OC00YTI0LWIxOTAtNmRkNTc0Nzg2YWU1XkEyXkFqcGdeQXVyNjY1MTg4Mzc@._V1_FMjpg_UX1000_.jpg", rating: 8.10),
    Movie(title: "Avengers: Infinity War", releaseYear: "2018", posterUrl: "https://media.comicbook.com/2018/03/avengers-infinity-war-poster-1093756.jpeg", rating: 9.10),
    Movie(title: "Five Feet Apart", releaseYear: "2019", posterUrl: "data:image/jpeg;base64,", rating: 8.10),
    Movie(title: "Avatar: The Way of Water", releaseYear: "2022", posterUrl: "data:image/jpeg;base64,", rating: 8.10),
    Movie(title: "La La Land", releaseYear: "2016", posterUrl: "data:image/jpeg;base64,", rating: 9.10),
    Movie(title: "Bridge to Terabithia", releaseYear: "2007", posterUrl: "https://m.media-amazon.com/images/M/MV5BMTMzOTk1MzIyN15BMl5BanBnXkFtZTcwNTM3MjczMQ@@._V1_.jpg", rating: 8.10),
    Movie(title: "10 Things I Hate About You", releaseYear: "1999", posterUrl: "data:image/jpeg;base64,", rating: 9.10),
    Movie(title: "Spider-Man: No Way Home", releaseYear: "2021", posterUrl: "https://cdn.marvel.com/content/1x/snh_online_6072x9000_posed_01.jpg", rating: 9.10),
    Movie(title: "Big Hero 6", releaseYear: "2014", posterUrl: "https://lumiere-a.akamaihd.net/v1/images/p_bighero6_19753_20bd6206.jpeg", rating: 8.10),
    Movie(title: "Encanto", releaseYear: "2021", posterUrl: "https://lumiere-a.akamaihd.net/v1/images/encanto-id-poster_b0f0a082.jpeg?region=0%2C0%2C600%2C900", rating: 8.10),
    Movie(title: "Frozen", releaseYear: "2013", posterUrl: "https://m.media-amazon.com/images/I/81zbSEXnDOL._AC_UF1000,1000_QL80_.jpg", rating: 9.10),
    Movie(title: "Tangled", releaseYear: "2010", posterUrl: "https://m.media-amazon.com/images/I/71-qC7IDkQL._AC_UF894,1000_QL80_.jpg", rating: 8.10),
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('✧ My Favourite Movies ✧'),
      ),
      body: ListView.builder(
        itemCount: movies.length,
        itemBuilder: (BuildContext context, int index) {
          return MovieCard(movie: movies[index]);
        },
      ),
    );
  }
}

class MovieCard extends StatelessWidget {
  final Movie movie;

  MovieCard({required this.movie});

  @override
  Widget build(BuildContext context) {
    return Card(
      margin: EdgeInsets.all(10.0),
      child: Row(
        children: [
          Container(
            width: 100,
            height: 150,
            child: Image.network(movie.posterUrl, fit: BoxFit.cover),
          ),
          SizedBox(width: 10),
          Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              Padding(
                padding: EdgeInsets.only(top: 8.0),
                child: Text(
                  movie.title,
                  style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold),
                ),
              ),
              Padding(
                padding: EdgeInsets.symmetric(vertical: 4.0),
                child: Text("Release Year: ${movie.releaseYear}"),
              ),
              Padding(
                padding: EdgeInsets.only(bottom: 8.0),
                child: Text(
                  "Rating: ${movie.rating}",
                  style: TextStyle(
                    color: Colors.blue,
                    fontSize: 14, //
                    fontWeight: FontWeight.w100,
                  ),
                ),
              ),
            ],
          ),
        ],
      ),
    );
  }
}

